import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import QuantityPopup from './QuantityPopup';
import './ProductList.css';

const ProductList = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [priceRange, setPriceRange] = useState({ min: 0, max: Infinity });
  const [sortOption, setSortOption] = useState('default');
  const [loading, setLoading] = useState(true);

  // 商品データの取得
  useEffect(() => {
    const fetchData = async () => {
      try {
        const itemNameResponse = await fetch('https://aearial.github.io/my-app/items/ItemName.csv');
        const itemNameText = await itemNameResponse.text();
        const itemNameData = Papa.parse(itemNameText, { header: false }).data;

        const itemFlavResponse = await fetch('https://aearial.github.io/my-app/items/ItemFlav.csv');
        const itemFlavText = await itemFlavResponse.text();
        const itemFlavData = Papa.parse(itemFlavText, { header: false }).data;

        const parsedProducts = itemNameData.map((row, index) => ({
          id: index + 1,
          name: row[1],
          description: itemFlavData[index] ? itemFlavData[index][1] : '説明なし',
          image: `https://aearial.github.io/my-app/items/${index + 1}.png`,
          price: 3 + index * 2, // ダミーデータ: 可変価格
        }));

        setProducts(parsedProducts);
        setFilteredProducts(parsedProducts);
        setLoading(false);
      } catch (error) {
        console.error('データの読み込みに失敗しました:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // フィルタリング
  useEffect(() => {
    let result = products;

    if (searchQuery) {
      const lowerCaseQuery = searchQuery.toLowerCase();
      result = result.filter(
        (product) =>
          product.name.toLowerCase().includes(lowerCaseQuery) ||
          product.description.toLowerCase().includes(lowerCaseQuery)
      );
    }

    result = result.filter(
      (product) =>
        product.price >= priceRange.min && product.price <= priceRange.max
    );

    if (sortOption === 'price-asc') {
      result = [...result].sort((a, b) => a.price - b.price);
    } else if (sortOption === 'price-desc') {
      result = [...result].sort((a, b) => b.price - a.price);
    }

    setFilteredProducts(result);
  }, [searchQuery, priceRange, sortOption, products]);

  if (loading) {
    return <div>データを読み込んでいます...</div>;
  }

  return (
    <div className="product-container">
      {/* サイドバー (検索メニュー) */}
      <div className="search-menu">
        <h2>検索メニュー</h2>

        {/* 検索バー */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="商品名や説明を検索..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        {/* 価格範囲フィルタ */}
        <div className="price-filter">
          <h3>価格範囲</h3>
          <input
            type="number"
            placeholder="最小価格"
            onChange={(e) =>
              setPriceRange((prev) => ({
                ...prev,
                min: Number(e.target.value) || 0,
              }))
            }
          />
          <input
            type="number"
            placeholder="最大価格"
            onChange={(e) =>
              setPriceRange((prev) => ({
                ...prev,
                max: Number(e.target.value) || Infinity,
              }))
            }
          />
        </div>

        {/* ソートオプション */}
        <div className="sort-options">
          <h3>ソート</h3>
          <select onChange={(e) => setSortOption(e.target.value)}>
            <option value="default">デフォルト</option>
            <option value="price-asc">価格の安い順</option>
            <option value="price-desc">価格の高い順</option>
          </select>
        </div>
      </div>

      {/* 商品リスト */}
      <div className="product-list">
        <h2>商品一覧</h2>
        <div className="product-grid">
          {filteredProducts.map((product) => (
            <div key={product.id} className="product-item">
              <img src={product.image} alt={product.name} className="product-image" />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>Cost: {product.price}</p>
              <button onClick={() => setSelectedProduct(product)}>選択</button>
            </div>
          ))}
        </div>
      </div>

      {/* ポップアップ */}
      {selectedProduct && (
        <QuantityPopup
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          addToCart={addToCart}
        />
      )}
    </div>
  );
};

export default ProductList;
