import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import './ProductList.css';

const ProductList = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [editedName, setEditedName] = useState('');
  const [editedDescription, setEditedDescription] = useState('');
  const [isEditingEnabled, setIsEditingEnabled] = useState(false);  // 編集有効状態
  const [password, setPassword] = useState('');  // パスワード入力状態
  const [showPasswordPopup, setShowPasswordPopup] = useState(false);  // パスワードポップアップの表示状態

  // CSVファイルを読み込み、商品の情報を取得
  useEffect(() => {
    const fetchData = async () => {
      try {
        // 商品名のCSV読み込み
        const itemNameResponse = await fetch('/items/ItemName.csv');
        const itemNameText = await itemNameResponse.text();
        const itemNameData = Papa.parse(itemNameText, { header: false }).data;

        // 商品説明のCSV読み込み
        const itemFlavResponse = await fetch('/items/ItemFlav.csv');
        const itemFlavText = await itemFlavResponse.text();
        const itemFlavData = Papa.parse(itemFlavText, { header: false }).data;

        // 商品名と説明を統合して商品リストを作成
        const parsedProducts = itemNameData.map((row, index) => ({
          id: index + 1,
          name: row[1],  // ITEMx_NAME
          description: itemFlavData[index] ? itemFlavData[index][1] : '説明なし',  // ITEMx_FLAV
          image: `/items/${index + 1}.png`,  // 商品画像
          price: Math.floor(Math.random() * 5000) + 1000,  // ランダム価格
        }));

        setProducts(parsedProducts);  // 商品リストの設定
      } catch (error) {
        console.error("データの読み込みに失敗しました:", error);
      }
    };

    fetchData();  // データを読み込む
  }, []);  // コンポーネントがマウントされるときに実行

  const handleEdit = (product) => {
    setSelectedProduct(product);
    setEditedName(product.name);
    setEditedDescription(product.description);
  };

  const handleSave = async () => {
    if (!editedName || !editedDescription) {
      alert("商品名と説明は必須です");
      return;
    }

    const updatedProduct = { ...selectedProduct, name: editedName, description: editedDescription };
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === selectedProduct.id ? updatedProduct : product
      )
    );
    setSelectedProduct(null);  // 編集を終了

    // CSVデータを更新
    const updatedItems = products.map((product) => ({
      name: product.name,
      description: product.description,
    }));

    // CSVに保存
    const csvContent = Papa.unparse(updatedItems);
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'updated_items.csv';
    a.click();
    URL.revokeObjectURL(url);  // URLの解放
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleEnableEditing = () => {
    setShowPasswordPopup(true);  // パスワードポップアップを表示
  };

  const handlePasswordSubmit = () => {
    if (password === 'admin11') {
      setIsEditingEnabled(true);  // 編集を有効にする
      setShowPasswordPopup(false);  // パスワードポップアップを閉じる
    } else {
      setIsEditingEnabled(false);  // 編集を無効にする
      setShowPasswordPopup(false);  // パスワードポップアップを閉じる
    }
  };

  return (
    <div className="product-list">
      <h2>商品一覧</h2>

      {/* 編集モードのチェックボックス */}
      <div className="edit-toggle">
        <label>
          <input
            type="checkbox"
            checked={isEditingEnabled}
            onChange={handleEnableEditing}
          />
          商品の編集を有効にする
        </label>
      </div>

      {/* パスワードポップアップ */}
      {showPasswordPopup && (
        <div className="password-popup">
          <div className="popup-content">
            <h3>パスワードを入力してください</h3>
            <input
              type="password"
              value={password}
              onChange={handlePasswordChange}
              placeholder="管理者パスワード"
            />
            <button onClick={handlePasswordSubmit}>送信</button>
          </div>
        </div>
      )}

      <div className="product-grid">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>¥{product.price}</p>

            {/* 編集可能な場合のみ表示 */}
            {isEditingEnabled && (
              <div>
                <button onClick={() => handleEdit(product)}>編集</button>
              </div>
            )}

            <button onClick={() => addToCart(product)}>カートに追加</button>
          </div>
        ))}
      </div>

      {selectedProduct && isEditingEnabled && (
        <div className="edit-popup">
          <div className="popup-content">
            <h3>商品を編集</h3>
            <label>
              商品名:
              <input
                type="text"
                value={editedName}
                onChange={(e) => setEditedName(e.target.value)}
              />
            </label>
            <label>
              商品説明:
              <textarea
                value={editedDescription}
                onChange={(e) => setEditedDescription(e.target.value)}
              />
            </label>
            <div className="popup-actions">
              <button onClick={handleSave}>保存</button>
              <button onClick={() => setSelectedProduct(null)}>キャンセル</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductList;
