import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import QuantityPopup from './QuantityPopup';
import './ProductList.css';

const ProductList = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(100);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [displaySize, setDisplaySize] = useState('medium');  // 初期表示サイズ

  // CSVファイルを読み込み、商品の情報を取得
  useEffect(() => {
    // キャッシュから商品データを取得
    const cachedProducts = localStorage.getItem('products');
    if (cachedProducts) {
      setProducts(JSON.parse(cachedProducts)); // キャッシュがあればそれを使う
      setFilteredProducts(JSON.parse(cachedProducts)); // フィルタリング後のデータも更新
    } else {
      // CSVファイルから商品データを読み込む（初回読み込み時）
      const fetchData = async () => {
        try {
          const itemNameResponse = await fetch('https://aearial.github.io/my-app/items/ItemName.csv');
          const itemNameText = await itemNameResponse.text();
          const itemNameData = Papa.parse(itemNameText, { header: false }).data;

          const itemFlavResponse = await fetch('https://aearial.github.io/my-app/items/ItemFlav.csv');
          const itemFlavText = await itemFlavResponse.text();
          const itemFlavData = Papa.parse(itemFlavText, { header: false }).data;

          const parsedProducts = itemNameData.map((row, index) => ({
            id: index + 1,
            name: row[1],
            description: itemFlavData[index] ? itemFlavData[index][1] : '説明なし',
            price: index % 5 + 3,
            image: `https://aearial.github.io/my-app/items/${index + 1}.png`,
          }));

          setProducts(parsedProducts);
          setFilteredProducts(parsedProducts);
          localStorage.setItem('products', JSON.stringify(parsedProducts)); // 商品データをキャッシュに保存
        } catch (error) {
          console.error('データの読み込みに失敗しました:', error);
        }
      };

      fetchData();
    }
  }, []);

  // 商品リストの表示フィルタリング
  useEffect(() => {
    const filtered = products.filter(
      (product) =>
        product.price >= minPrice &&
        product.price <= maxPrice &&
        product.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    const sorted = filtered.sort((a, b) => {
      if (sortOrder === 'asc') {
        return a.price - b.price;
      } else {
        return b.price - a.price;
      }
    });

    setFilteredProducts(sorted);
  }, [products, minPrice, maxPrice, searchQuery, sortOrder]);

  // 商品サイズを選択
  const handleDisplaySizeChange = (event) => {
    setDisplaySize(event.target.value);
  };

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div className="product-list">
      <div className="filters">
        {/* フィルタフォーム */}
        <input
          type="text"
          placeholder="検索"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <input
          type="number"
          placeholder="最低価格"
          value={minPrice}
          onChange={(e) => setMinPrice(parseInt(e.target.value))}
        />
        <input
          type="number"
          placeholder="最大価格"
          value={maxPrice}
          onChange={(e) => setMaxPrice(parseInt(e.target.value))}
        />
        <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
          <option value="asc">価格昇順</option>
          <option value="desc">価格降順</option>
        </select>
      </div>
      {/* 商品一覧 */}
      <div className={`product-items ${displaySize}`}>
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>¥{product.price}</p>
            <button onClick={() => handleAddToCart(product)}>カートに追加</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
