import React, { useState } from 'react';
import ProductList from './ProductList';
import Cart from './Cart';
import QuantityPopup from './QuantityPopup'; ト

const App = () => {
  const [activeTab, setActiveTab] = useState('products'); // タブの状態
  const [cartItems, setCartItems] = useState([]); // カートの商品
  const [showPopup, setShowPopup] = useState(false); // ポップアップの表示制御
  const [selectedProduct, setSelectedProduct] = useState(null); // 選択された商品情報

  // カートに商品を追加
  const addToCart = (product) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find((item) => item.id === product.id);
      if (existingItem) {
        return prevItems.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + product.quantity }
            : item
        );
      }
      return [...prevItems, product];
    });
    setShowPopup(false); // カートに追加後、ポップアップを閉じる
  };

  const handleAddToCartClick = (product) => {
    setSelectedProduct(product); // 選択した商品情報を保存
    setShowPopup(true); // ポップアップを表示
  };

  return (
    <div>
      {/* タブメニュー */}
      <div className="tab-menu">
        <button
          className={activeTab === 'products' ? 'active-tab' : ''}
          onClick={() => setActiveTab('products')}
        >
          商品一覧
        </button>
        <button
          className={activeTab === 'cart' ? 'active-tab' : ''}
          onClick={() => setActiveTab('cart')}
        >
          カート
        </button>
      </div>

      {/* タブ内容 */}
      <div className="tab-content">
        {activeTab === 'products' && (
          <>
            <ProductList addToCart={handleAddToCartClick} />
            {showPopup && <QuantityPopup product={selectedProduct} onClose={() => setShowPopup(false)} addToCart={addToCart} />}
          </>
        )}
        {activeTab === 'cart' && <Cart cartItems={cartItems} />}
      </div>
    </div>
  );
};

export default App;
