// ProductList.js
import React, { useState } from 'react';
import QuantityPopup from './QuantityPopup';  // 数量選択のポップアップをインポート
import './ProductList.css';  // スタイルを別ファイルに分ける

const ProductList = ({ products, addToCart }) => {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const openQuantityPopup = (product) => {
    setSelectedProduct(product);  // 商品の情報をポップアップに渡す
  };

  const closeQuantityPopup = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="product-list-container">
      <h2>商品一覧</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>¥{product.price}</p>
            <button onClick={() => openQuantityPopup(product)} className="add-button">
              数量を選ぶ
            </button>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <QuantityPopup
          product={selectedProduct}
          onClose={closeQuantityPopup}
          addToCart={addToCart}
        />
      )}
    </div>
  );
};

export default ProductList;
