import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import QuantityPopup from './QuantityPopup';
import './ProductList.css';

const ProductList = ({ addToCart, isSwitchOn }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState('');
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(100);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [displaySize, setDisplaySize] = useState('medium');

  // 商品データを取得
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const cachedProducts = localStorage.getItem('products');
        if (cachedProducts && !isSwitchOn) {
          setNotification('ローカルストレージからデータを読み込んでいます...');
          const productsData = JSON.parse(cachedProducts);
          setProducts(productsData);
          setFilteredProducts(productsData);
        } else {
          setNotification('データを取得しています...');
          const itemNameResponse = await fetch(
            isSwitchOn ? '@/items/ItemName.csv' : 'https://aearial.github.io/my-app/items/ItemName.csv'
          );
          const itemNameText = await itemNameResponse.text();
          const itemNameData = Papa.parse(itemNameText, { header: false }).data;

          const itemFlavResponse = await fetch(
            isSwitchOn ? './items/ItemFlav.csv' : 'https://aearial.github.io/my-app/items/ItemFlav.csv'
          );
          const itemFlavText = await itemFlavResponse.text();
          const itemFlavData = Papa.parse(itemFlavText, { header: false }).data;

          const parsedProducts = itemNameData.map((row, index) => ({
            id: index + 1,
            name: row[1],
            description: itemFlavData[index] ? itemFlavData[index][1] : '説明なし',
            image: isSwitchOn
              ? `/items/${index + 1}.png`
              : `https://aearial.github.io/my-app/items/${index + 1}.png`,
            price: (index % 5 + 3) * 10,
          }));

          setProducts(parsedProducts);
          setFilteredProducts(parsedProducts);
          localStorage.setItem('products', JSON.stringify(parsedProducts));
        }
      } catch (error) {
        console.error('データの読み込みに失敗しました:', error);
        setNotification('データの読み込みに失敗しました');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [isSwitchOn]); // isSwitchOnが切り替わったら再取得

  // フィルタリングとソート
  const filterAndSortProducts = () => {
    let filtered = products.filter((product) => {
      return (
        (product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          product.description.toLowerCase().includes(searchQuery.toLowerCase())) &&
        product.price >= minPrice &&
        product.price <= maxPrice
      );
    });

    if (sortOrder === 'asc') {
      filtered.sort((a, b) => a.price - b.price);
    } else {
      filtered.sort((a, b) => b.price - a.price);
    }

    setFilteredProducts(filtered);
  };

  // 検索条件が変更されたとき
  useEffect(() => {
    filterAndSortProducts();
  }, [searchQuery, minPrice, maxPrice, sortOrder]);

  // ローカルストレージをクリアする関数
  const clearLocalStorage = () => {
    localStorage.removeItem('products');
    setNotification('ローカルストレージがクリアされました');
  };

  if (loading) {
    return <div>{notification}</div>;
  }

  return (
    <div className="product-list-container">
      {/* 制御ボタン */}
      <div className="controls">
        <button onClick={() => setSortOrder('asc')}>価格昇順</button>
        <button onClick={() => setSortOrder('desc')}>価格降順</button>
        <button onClick={clearLocalStorage}>ローカルストレージをクリア</button>

        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="商品名・説明で検索"
        />
      </div>

      {/* 商品リスト */}
      <div className="product-list">
        <h2>商品一覧</h2>
        {notification && <p>{notification}</p>}

        <div className="product-grid">
          {filteredProducts.map((product) => (
            <div
              key={product.id}
              className="product-item"
              onClick={() => setSelectedProduct(product)}
            >
              <img
                src={product.image}
                alt={product.name}
                className="product-image"
                style={{
                  width:
                    displaySize === 'small'
                      ? '70px'
                      : displaySize === 'medium'
                      ? '90px'
                      : '120px',
                }}
              />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>価格: {product.price}</p>
            </div>
          ))}
        </div>
        {selectedProduct && (
          <QuantityPopup
            product={selectedProduct}
            onClose={() => setSelectedProduct(null)}
            addToCart={addToCart}
          />
        )}
      </div>
    </div>
  );
};

export default ProductList;
