// App.js
import React, { useState } from 'react';
import ProductList from './ProductList';
import Cart from './Cart';
import './App.css';  // 全体のスタイル

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const products = [
    { id: 1, name: '商品A', price: 1000, image: 'https://via.placeholder.com/150' },
    { id: 2, name: '商品B', price: 2000, image: 'https://via.placeholder.com/150' },
    { id: 3, name: '商品C', price: 3000, image: 'https://via.placeholder.com/150' },
  ];

  const addToCart = (product) => {
    setCartItems((prevItems) => {
      const existingItemIndex = prevItems.findIndex((item) => item.id === product.id);
      if (existingItemIndex >= 0) {
        const updatedItems = [...prevItems];
        updatedItems[existingItemIndex].quantity += product.quantity;
        return updatedItems;
      } else {
        return [...prevItems, product];
      }
    });
  };

  return (
    <div className="app-container">
      <ProductList products={products} addToCart={addToCart} />
      <Cart cartItems={cartItems} />
    </div>
  );
};

export default App;
