import React, { useState } from 'react';
import ProductList from './ProductList';
import Cart from './Cart';
import EditProducts from './EditProducts'; // 編集画面のインポート
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('products');
  const [cartItems, setCartItems] = useState([]);

  // カートに商品を追加する関数
  const addToCart = (product, quantity) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find((item) => item.id === product.id);
      if (existingItem) {
        // 同じ商品がカートにある場合、数量を更新
        return prevItems.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
      }
      // 新しい商品をカートに追加
      return [...prevItems, { ...product, quantity }];
    });
  };

  return (
    <div>
      {/* タブメニュー */}
      <div className="tab-menu">
        <button
          className={activeTab === 'products' ? 'active-tab' : ''}
          onClick={() => setActiveTab('products')}
        >
          商品一覧
        </button>
        <button
          className={activeTab === 'cart' ? 'active-tab' : ''}
          onClick={() => setActiveTab('cart')}
        >
          カート
        </button>
        <button
          className={activeTab === 'edit' ? 'active-tab' : ''}  // 編集タブ
          onClick={() => setActiveTab('edit')}
        >
          編集
        </button>
      </div>

      {/* タブ内容 */}
      <div className="tab-content">
        {activeTab === 'products' && <ProductList addToCart={addToCart} />}
        {activeTab === 'cart' && <Cart cartItems={cartItems} setCartItems={setCartItems} />}
        {activeTab === 'edit' && <EditProducts />}  {/* 編集画面を表示 */}
      </div>
    </div>
  );
};

export default App;
