import React, { useState } from 'react';
import './Cart.css'; // カートのスタイルを追加

const Cart = ({ cartItems, setCartItems }) => {
  const [selectedItems, setSelectedItems] = useState([]); // 選択された商品を保持

  // 合計金額の計算
  const totalAmount = cartItems.reduce(
    (total, item) => total + item.price * item.quantity,
    0
  );

  const handleCheckboxChange = (itemId) => {
    setSelectedItems((prevSelectedItems) => {
      // 商品が選ばれているかチェックして、選択リストを更新
      if (prevSelectedItems.includes(itemId)) {
        return prevSelectedItems.filter((id) => id !== itemId); // 既に選ばれていれば削除
      } else {
        return [...prevSelectedItems, itemId]; // 新たに選ばれた場合、リストに追加
      }
    });
  };

  const handleRemoveSelectedItems = () => {
    // 選択されたアイテムを削除
    const updatedCartItems = cartItems.filter(
      (item) => !selectedItems.includes(item.id)
    );
    setCartItems(updatedCartItems); // カートアイテムを更新
    setSelectedItems([]); // 選択されたアイテムリストをリセット
  };

  return (
    <div className="cart">
      <h2>カート</h2>

      {cartItems.length === 0 ? (
        <p>カートにアイテムがありません。</p>
      ) : (
        <div className="cart-items">
          <ul>
            {cartItems.map((item) => (
              <li key={item.id} className="cart-item">
                {/* チェックボックス */}
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.id)}
                  onChange={() => handleCheckboxChange(item.id)}
                  className="cart-item-checkbox"
                />

                {/* 商品画像と商品名 */}
                <div className="cart-item-image-container">
                  <img src={item.image} alt={item.name} className="cart-item-image" />
                  <div className="cart-item-name">{item.name}</div>
                </div>

                {/* 単価と数量 */}
                <div className="cart-item-pricing">
                  <p>Cost: {item.price}</p>
                  <p>数量: {item.quantity}</p>
                </div>

                {/* 小計 */}
                <div className="cart-item-subtotal">
                  小計: {item.price * item.quantity}
                </div>
              </li>
            ))}
          </ul>

          <div className="cart-total">
            <h3>合計Cost: {totalAmount}</h3>
            <button className="checkout-button">購入手続きへ進む</button>
          </div>
        </div>
      )}

      {/* 削除ボタン (選択された商品を削除) */}
      {selectedItems.length > 0 && (
        <button className="remove-button" onClick={handleRemoveSelectedItems}>
          選択した商品を削除
        </button>
      )}
    </div>
  );
};

export default Cart;
