import React, { useState } from 'react';
import Papa from 'papaparse';
import './EditProducts.css';
import EditProductModal from './EditProductModal';

const EditProducts = () => {
  const [products, setProducts] = useState([]);
  const [editProduct, setEditProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', price: 1000, image: '' });

  // CSVファイルを取り込み
  const handleFileUpload = (file, type) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const data = Papa.parse(e.target.result, { header: false }).data;

      if (type === 'ItemName') {
        // 商品名を更新
        const updatedProducts = data.map((row, index) => ({
          id: index + 1,
          name: row[1] || '', // 商品名
          description: products[index]?.description || '', // 既存の説明
          price: products[index]?.price || '', // 既存の価格
          image: products[index]?.image || '', // 既存の画像
        }));
        setProducts(updatedProducts);
      } else if (type === 'ItemFlav') {
        // 商品説明を更新
        const updatedProducts = products.map((product, index) => ({
          ...product,
          description: data[index] ? data[index][1] : product.description,
        }));
        setProducts(updatedProducts);
      }
    };
    reader.readAsText(file);
  };

  // 新規商品の追加
  const handleAddProduct = () => {
    const newId = products.length + 1;
    const updatedProducts = [...products, { ...newProduct, id: newId }];
    setProducts(updatedProducts);
    setNewProduct({ name: '', description: '', price: 4, image: '' });
  };

  // 編集データの保存
  const handleSaveEdit = (updatedProduct) => {
    const updatedProducts = products.map((p) => (p.id === updatedProduct.id ? updatedProduct : p));
    setProducts(updatedProducts);
    setEditProduct(null);
  };

  return (
    <div className="edit-products">
      <h2>商品の追加‐変更</h2>

      {/* CSV取り込み */}
      <div className="csv-upload">
        <h3>CSVを取り込む</h3>
        <div>
          <label htmlFor="itemNameCSV">商品名CSV:</label>
          <input
            type="file"
            id="itemNameCSV"
            accept=".csv"
            onChange={(e) => handleFileUpload(e.target.files[0], 'ItemName')}
          />
        </div>
        <div>
          <label htmlFor="itemFlavCSV">商品説明CSV:</label>
          <input
            type="file"
            id="itemFlavCSV"
            accept=".csv"
            onChange={(e) => handleFileUpload(e.target.files[0], 'ItemFlav')}
          />
        </div>
      </div>

      {/* 新規商品追加 */}
      <div className="new-product-form">
        <h3>新規商品を追加</h3>
        <input
          type="text"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
          placeholder="商品名"
        />
        <input
          type="text"
          value={newProduct.description}
          onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
          placeholder="説明"
        />
        <input
          type="number"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: parseFloat(e.target.value) })}
          placeholder="価格"
        />
        <input
          type="text"
          value={newProduct.image}
          onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })}
          placeholder="画像URL"
        />
        <button onClick={handleAddProduct}>商品を追加</button>
      </div>

      {/* 商品リスト */}
      <h2>以下、選択して編集可能</h2>
      <div className="product-table">
        {products.map((product) => (
          <div key={product.id} className="product-row" onClick={() => setEditProduct(product)}>
            <div className="product-info">
              {product.image && <img src={product.image} alt={product.name} className="product-image" />}
              <span>{product.name}</span>
              <span>{product.description}</span>
              <span>Cost: {product.price}</span>
            </div>
          </div>
        ))}
      </div>

      {/* 編集モーダル */}
      {editProduct && (
        <EditProductModal
          product={editProduct}
          onSave={handleSaveEdit}
          onClose={() => setEditProduct(null)}
        />
      )}
    </div>
  );
};

export default EditProducts;
