import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import './ProductList.css';

const ProductList = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [searchText, setSearchText] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [displaySize, setDisplaySize] = useState('medium');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const itemNameResponse = await fetch('https://aearial.github.io/my-app/items/ItemName.csv');
        const itemNameText = await itemNameResponse.text();
        const itemNameData = Papa.parse(itemNameText, { header: false }).data;

        const itemFlavResponse = await fetch('https://aearial.github.io/my-app/items/ItemFlav.csv');
        const itemFlavText = await itemFlavResponse.text();
        const itemFlavData = Papa.parse(itemFlavText, { header: false }).data;

        const parsedProducts = itemNameData.map((row, index) => ({
          id: index + 1,
          name: row[1],
          description: itemFlavData[index] ? itemFlavData[index][1] : '説明なし',
          image: `https://aearial.github.io/my-app/items/${index + 1}.png`,
          price: 3,
        }));

        setProducts(parsedProducts);
        setLoading(false);
      } catch (error) {
        console.error("データの読み込みに失敗しました:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearchChange = (event) => {
    setSearchText(event.target.value);
  };

  const handleSortChange = (order) => {
    setSortOrder(order);
  };

  const handleDisplaySizeChange = (size) => {
    setDisplaySize(size);
  };

  const filteredProducts = products
    .filter((product) =>
      product.name.toLowerCase().includes(searchText.toLowerCase()) ||
      product.description.toLowerCase().includes(searchText.toLowerCase())
    )
    .sort((a, b) => {
      if (sortOrder === 'asc') {
        return a.price - b.price;
      } else {
        return b.price - a.price;
      }
    });

  const displayClass = displaySize === 'small' ? 'product-item small' :
    displaySize === 'medium' ? 'product-item medium' : 'product-item large';

  if (loading) {
    return <div>データを読み込んでいます...</div>;
  }

  return (
    <div className="product-list-container">
      <div className="controls">
        <div className="section">
          <label>検索</label>
          <input type="text" value={searchText} onChange={handleSearchChange} />
        </div>
        <div className="section sort-buttons">
          <label>ソート</label>
          <button onClick={() => handleSortChange('asc')}>昇順</button>
          <button onClick={() => handleSortChange('desc')}>降順</button>
        </div>
        <div className="section">
          <label>表示サイズ変更</label>
          <button onClick={() => handleDisplaySizeChange('small')}>小</button>
          <button onClick={() => handleDisplaySizeChange('medium')}>中</button>
          <button onClick={() => handleDisplaySizeChange('large')}>大</button>
        </div>
      </div>

      <h2>商品一覧</h2>
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className={displayClass}
            onClick={() => setSelectedProduct(product)}
          >
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <div className="product-info">
              <p>商品番号: {product.id}</p>
              <p className="product-price">金額: ¥{product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
