import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import './EditProducts.css';

const EditProducts = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', price: 1000 });
  const [editMode, setEditMode] = useState(null);  // 編集モードのトラッキング

  // CSVファイルを読み込み、商品の情報を取得
  useEffect(() => {
    const fetchData = async () => {
      try {
        const itemNameResponse = await fetch('/items/ItemName.csv');
        const itemNameText = await itemNameResponse.text();
        const itemNameData = Papa.parse(itemNameText, { header: false }).data;

        const itemFlavResponse = await fetch('/items/ItemFlav.csv');
        const itemFlavText = await itemFlavResponse.text();
        const itemFlavData = Papa.parse(itemFlavText, { header: false }).data;

        const parsedProducts = itemNameData.map((row, index) => ({
          id: index + 1,
          name: row[1],  // ITEMx_NAME
          description: itemFlavData[index] ? itemFlavData[index][1] : '説明なし',  // ITEMx_FLAV
          price: 1000,  // 価格を仮定
        }));

        setProducts(parsedProducts);
      } catch (error) {
        console.error("データの読み込みに失敗しました:", error);
      }
    };

    fetchData();
  }, []);

  // 商品名と説明を変更
  const handleChange = (index, field, value) => {
    const updatedProducts = [...products];
    updatedProducts[index][field] = value;
    setProducts(updatedProducts);
  };

  // 新規商品を追加
  const handleAddProduct = () => {
    setProducts([
      ...products,
      { id: products.length + 1, name: newProduct.name, description: newProduct.description, price: newProduct.price }
    ]);
    setNewProduct({ name: '', description: '', price: 1000 });
  };

  // 編集モードの切り替え
  const toggleEditMode = (index) => {
    setEditMode(editMode === index ? null : index);  // クリックした商品だけ編集モードを切り替え
  };

  // CSVダウンロード
  const handleDownload = () => {
    const itemNames = products.map((product) => [product.id, product.name]);
    const itemFlavs = products.map((product) => [product.id, product.description]);

    const itemNameCSV = Papa.unparse(itemNames);
    const itemFlavCSV = Papa.unparse(itemFlavs);

    const downloadItemNameLink = document.createElement('a');
    downloadItemNameLink.href = 'data:text/csv;charset=utf-8,' + encodeURIComponent(itemNameCSV);
    downloadItemNameLink.download = 'ItemName.csv';
    downloadItemNameLink.click();

    const downloadItemFlavLink = document.createElement('a');
    downloadItemFlavLink.href = 'data:text/csv;charset=utf-8,' + encodeURIComponent(itemFlavCSV);
    downloadItemFlavLink.download = 'ItemFlav.csv';
    downloadItemFlavLink.click();
  };

  // CSV保存リクエスト
  const handleSave = async () => {
    const itemNames = products.map((product) => [product.id, product.name]);
    const itemFlavs = products.map((product) => [product.id, product.description]);

    const itemNameCSV = Papa.unparse(itemNames);
    const itemFlavCSV = Papa.unparse(itemFlavs);

    try {
      await fetch('http://localhost:3001/save-item-name', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ itemNameCSV }),
      });

      await fetch('http://localhost:3001/save-item-flav', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ itemFlavCSV }),
      });

      alert('データが保存されました');
    } catch (error) {
      console.error('保存エラー:', error);
      alert('保存に失敗しました');
    }
  };

  return (
    <div className="edit-products">
      <h2>商品編集</h2>
      <div className="product-table">
        {products.map((product, index) => (
          <div key={product.id} className="product-row">
            <button onClick={() => toggleEditMode(index)} className="edit-button">
              {editMode === index ? '編集終了' : '編集'}
            </button>

            {editMode === index ? (
              <>
                <input
                  type="text"
                  value={product.name}
                  onChange={(e) => handleChange(index, 'name', e.target.value)}
                  className="product-name"
                />
                <input
                  type="text"
                  value={product.description}
                  onChange={(e) => handleChange(index, 'description', e.target.value)}
                  className="product-description"
                />
                <input
                  type="number"
                  value={product.price}
                  onChange={(e) => handleChange(index, 'price', e.target.value)}
                  className="product-price"
                />
              </>
            ) : (
              <>
                <span>{product.name}</span>
                <span>{product.description}</span>
                <span>{product.price}円</span>
              </>
            )}
          </div>
        ))}
      </div>
      
      {/* 新規商品追加 */}
      <div className="new-product">
        <input
          type="text"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
          placeholder="新商品名"
        />
        <input
          type="text"
          value={newProduct.description}
          onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
          placeholder="新商品説明"
        />
        <input
          type="number"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          placeholder="Cost"
        />
        <button onClick={handleAddProduct}>商品追加</button>
      </div>

      <button onClick={handleDownload}>CSVをダウンロード</button>
      <button onClick={handleSave}>保存</button>
    </div>
  );
};

export default EditProducts;
