import React, { useState } from 'react';
import ProductList from './ProductList';
import Cart from './Cart';
import EditProducts from './EditProducts';
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('products');
  const [cartItems, setCartItems] = useState([]);
  const [isSwitchOn, setIsSwitchOn] = useState(false); // スイッチの状態をAppで管理

  const ToggleSwitch = ({ isOn, onToggle }) => {
    return (
      <div className="toggle-container">
        <div
          className={`toggle-switch ${isOn ? 'on' : ''}`}
          onClick={onToggle}
        >
          <div className={`toggle-slider ${isOn ? 'on' : 'off'}`}></div>
        </div>
        <p className={`toggle-text ${isOn ? 'on' : 'off'}`}>
          {isOn ? 'ON' : 'OFF'}
        </p>
      </div>
    );
  };

  const addToCart = (product) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find((item) => item.id === product.id);
      if (existingItem) {
        return prevItems.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + product.quantity }
            : item
        );
      }
      return [...prevItems, product];
    });
  };

  const handleSwitchToggle = () => {
    setIsSwitchOn((prev) => !prev); // スイッチの状態を切り替える
  };

  return (
    <div>
      {/* タブメニュー */}
      <div className="tab-menu">
        <button
          className={activeTab === 'products' ? 'active-tab' : ''}
          onClick={() => setActiveTab('products')}
        >
          商品一覧
        </button>
        <button
          className={activeTab === 'cart' ? 'active-tab' : ''}
          onClick={() => setActiveTab('cart')}
        >
          カート
        </button>
        <button
          className={activeTab === 'edit' ? 'active-tab' : ''}
          onClick={() => setActiveTab('edit')}
        >
          編集
        </button>
        {/* ToggleSwitchに状態とイベントハンドラを渡す */}
        <ToggleSwitch isOn={isSwitchOn} onToggle={handleSwitchToggle} />
      </div>

      {/* タブ内容 */}
      <div className="tab-content">
        {/* ProductListにスイッチの状態を渡す */}
        {activeTab === 'products' && (
          <ProductList addToCart={addToCart} isSwitchOn={isSwitchOn} />
        )}
        {activeTab === 'cart' && (
          <Cart cartItems={cartItems} setCartItems={setCartItems} />
        )}
        {activeTab === 'edit' && <EditProducts />}
      </div>
    </div>
  );
};

export default App;
